import React, { Component } from 'react';
import util from '../util'
export default class Basket extends Component {
    render() {
        const { cartItems } = this.props;
        var money = 1000;
        var string = "TOTAL TEAM POWER";
        var result = string.fontcolor("green");

        return (
            <div className="alert alert-info">
            {<div> YOUR TEAM </div>}
                {cartItems.length === 0
                    ? "Team is empty!" :
                    <div>You have {cartItems.length} member(s) in your team! <hr /></div>
                }
                {cartItems.length > 0 &&
                    <div>
                        <ul style={{ marginLeft: 5 }}>
                            {cartItems.map(item => (
                                <li key={item.id}>
                                    <b>{item.title}</b>
                                    <button style={{ float: 'right' }} className="btn btn-danger btn-xs"
                                        onClick={(e) => this.props.handleRemoveFromCart(e, item)}>X</button>
                                    <br />
                                    {item.count} X {util.formatCurrency(item.price)}
                                </li>))
                            }

                        </ul>

                        <b>this.result {util.formatCurrency(cartItems.reduce((a, c) => (a + c.price * c.count), 0))}</b>

                    </div>
                }
            </div>
        )
    }
}
